mapper.select:
select * from t_customer_host t
where t.ip_add=#{ipAdd} and t.mac_add=#{macAddress}

    and t.offline_time is null


    and to_char(t.online_time,'yyyymmddhh24miss')=#{onlineTime}

mapper.update:
update t_customer_host t
t.customer_id=#{cid}, t.ip_add=#{ipAdd}, t.mac_add=#{macAddress}, t.online_time=to_date(#{onlineTime},'yyyy-mm-dd hh24:mi:ss'), t.offline_time=to_date(#{offlineTime},'yyyy-mm-dd hh24:mi:ss'), t.os_system=#{osSystem}, t.site_code=#{siteCode}, t.version=#{version}
where t.site_code=#{siteCode} and t.ip_add=#{ipAdd} and t.mac_add=#{macAddress} and to_char(t.online_time,'yyyymmddhh24miss')=#{onlineTime}

update t_customer_host t

    t.offline_time=to_date(#{closeTime},'yyyy-mm-dd hh24:mi:ss')

where t.site_code=#{siteCode}
