mapper.update:
update t_customer t
t.account_id=#{accountId}, t.site_code=#{siteCode}, t.province_code=#{provinceCode}, t.city_code=#{cityCode}, t.county_code=#{countyCode}, t.name=#{name}, t.cert_type=#{certType}, t.cert_id=#{certId}, t.auth_org=#{authOrg}, t.nationality=#{nationality}, t.open_time=to_date(#{openTime},'yyyy-mm-dd hh24:mi:ss'), t.close_time=to_date(#{closeTime},'yyyy-mm-dd hh24:mi:ss'), t.status=#{status}
where t.site_code=#{siteCode} and t.account_id=#{accountId} and to_char(t.open_time,'yyyy-mm-dd hh24:mi:ss')=#{openTime}
