mapper.select:
select * from t_customer_host t
where t.ip_add=#{ipAdd} and t.mac_add=#{macAddress}

    and t.offline_time is null


    and to_char(t.online_time,'yyyy-mm-dd hh24:mi:ss')=#{onlineTime}

mapper.update:
update t_customer_host t
t.customer_id=#{cid}, t.ip_add=#{ipAdd}, t.mac_add=#{macAddress}, t.online_time=to_date(#{onlineTime},'yyyy-mm-dd hh24:mi:ss'), t.offline_time=to_date(#{offlineTime},'yyyy-mm-dd hh24:mi:ss'), t.os_system=#{osSystem}, t.version=#{version}
where t.ip_add=#{ipAdd} and t.mac_add=#{macAddress} and t.offline_time is null
