INSERT INTO t_alarm_history (customer_id,customer_name,host_ip,alarm_time,is_block,customer_type,alarm_type,alarm_level,alarm_content)SELECT AA.* FROM(

	(select #{item.customerId},#{item.customerName},#{item.hostIp},#{item.recordTime},#{item.blocked},#{item.customerType},#{item.alarmType},#{item.alarmLevel},#{item.alarmContent} from dual)
) AA
